mapper.insert:
INSERT INTO user (
id
, password
, avatar_url
, email
, phone
, gender
, fullname
, birthday
, address
, role_id
, created_at
, updated_at
) VALUES (
  #{id}
, #{password}
, #{avatarUrl}
, #{email}
, #{phone}
, #{gender}
, #{fullname}
, #{birthday}
, #{address}
, #{role_id}
, #{created_at}
, #{updated_at}
)

mapper.update:
UPDATE user

	ID=#{id}

		, avatar_url=#{avatarUrl}


		, email=#{email}


		, phone=#{phone}


		, gender=#{gender}


		, fullname=#{fullname}


		, birthday=#{birthday}


		, address=#{address}


		, role_id=#{role_id}


		, password=#{password}


WHERE id = #{id}
